/*!50003 CREATE*/ /*!50017 DEFINER=`root`@`localhost`*/ /*!50003 TRIGGER check_max_rentals
BEFORE INSERT ON user_book_status
FOR EACH ROW
BEGIN
    
    DECLARE max_rentals INT DEFAULT 0;
    DECLARE start_date DATE;
    DECLARE end_date DATE;
    
    
    SELECT Role INTO @role FROM user WHERE User_ID = NEW.User_ID;
    
    IF @role = 'Student' THEN
        SET max_rentals := 2;
    ELSEIF @role = 'Teacher' THEN
        SET max_rentals := 1;
    END IF;
    
    
    SET start_date := DATE_SUB(CURRENT_DATE, INTERVAL WEEKDAY(CURRENT_DATE) DAY);
    SET end_date := DATE_ADD(start_date, INTERVAL 6 DAY);
    
    
    IF (
        SELECT COUNT(*)
        FROM user_book_status
        WHERE User_ID = NEW.User_ID
          AND Type IN ('Application', 'Waiting')
          AND NEW.Type IN ('Application', 'Waiting')
          AND (Start_Date BETWEEN start_date AND end_date OR End_Date BETWEEN start_date AND end_date)
      ) >= max_rentals THEN
        SIGNAL SQLSTATE '45000'
            SET MESSAGE_TEXT = 'You have reached the maximum number of book requests for the week.',
                MYSQL_ERRNO = 1645;
    END IF;

    
    IF (
        SELECT COUNT(*)
        FROM user_book_status
        WHERE User_ID = NEW.User_ID
          AND Book_ID = NEW.Book_ID
          AND Type = 'Rent'
          AND (Start_Date BETWEEN start_date AND end_date OR End_Date BETWEEN start_date AND end_date)
      ) > 0 THEN
        SIGNAL SQLSTATE '45000'
            SET MESSAGE_TEXT = 'You cannot make an application for a book that you have in your possession.',
                MYSQL_ERRNO = 1646;
    END IF;

    
    IF (
        SELECT COUNT(*)
        FROM user_book_status
        WHERE User_ID = NEW.User_ID
          AND Type = 'Late'
      ) > 0 THEN
        SIGNAL SQLSTATE '45000'
            SET MESSAGE_TEXT = 'You cannot make an application for a book if you have delayed the return of a book.',
                MYSQL_ERRNO = 1647;
    END IF;

    
    IF (
        SELECT COUNT(*)
        FROM user_book_status
        WHERE User_ID = NEW.User_ID
          AND Book_ID = NEW.Book_ID
          AND Type IN ('Application', 'Waiting')
          AND (Start_Date BETWEEN start_date AND end_date OR End_Date BETWEEN start_date AND end_date)
      ) > 0 THEN
        SIGNAL SQLSTATE '45000'
            SET MESSAGE_TEXT = 'You cannot request a book that you have an active request for.',
                MYSQL_ERRNO = 1648;
    END IF;
END */;;
